import React from "react";

const padLeadiingZero = (number, size) => {
  const finalText = number.toString();
  while (finalText.length < size) {
    finalText = "0" + finalText;
  }
  return finalText;
};

const TimeCode = (props) => {
  const reelStandard = props.standard;
  const framePerSec = reelStandard === "PAL" ? 25 : 30;
  // Todo: Handle props.standard is neither "PAL" nir "NTSC"
  const totalFrames = +props.duration;
  console.log(totalFrames);
  const ff = Math.floor(totalFrames % framePerSec);
  const ffText = padLeadiingZero(ff);
  console.log(ffText);
  const ss = Math.floor(totalFrames % (60 * framePerSec));
  const ssText = padLeadiingZero(ss);
  console.log(ssText);
  const mm = Math.floor(totalFrames % (60 * 60 * framePerSec));
  const mmText = padLeadiingZero(mm);
  console.log(mmText);
  const hh = Math.floor(totalFrames / (60 * 60 * framePerSec));
  const hhText = padLeadiingZero(hh);
  console.log(hhText);

  return <p>{hhText + ":" + mmText + ":" + ssText + ":" + ffText}</p>;
};

export default TimeCode;
