import { Fragment } from "react";
import React from "react";
import ReelItem from "./ReelItem";

const Reels = (props) => {
  const selectReelHandler = (selectedReel) => {
    props.onSelectReel(selectedReel);
  };

  const reelVideos = (reel) => {
    const reelVideos = [];
    props.videos.Foreah((video) => {
      if (reel.videoIds.includes(video.id)) {
        reelVideos = [video, ...reelVideos];
      }
    });
    return reelVideos;
  };

  return (
    <Fragment>
      {props.items.map((reel) => {
        return (
          <ReelItem
            key={reel.name}
            source={reel}
            videos={reelVideos}
            onSelectReel={selectReelHandler}
          />
        );
      })}
    </Fragment>
  );
};

export default Reels;
