import { Fragment, useState } from "react";
import React from "react";
import Reels from "../components/Reels/Reels";
import Videos from "../components/Videos/Videos";
import NewReel from "./NewReel";

// Todo: Apply styles
const AllReels = (props) => {
  const [filteredVideos, setFilteredVideos] = useState([]);

  const displayReelVideosHandler = (selectedReel) => {
    let reelVideos = [];
    props.videos.forEach((video) => {
      if (selectedReel.videoIds.includes(video.id)) {
        reelVideos = [...reelVideos, video];
      }
    });
    setFilteredVideos(reelVideos);
  };

  return (
    <Fragment>
      <NewReel />
      <h1>All Reels</h1>
      {props.reels.length > 0 && (
        <div>
          <Reels
            items={props.reels}
            onSelectReel={displayReelVideosHandler}
            videos={props.videos}
            onChangeReelVideos={displayReelVideosHandler}
          />
        </div>
      )}
      <div>
        <Videos items={filteredVideos} />
      </div>
    </Fragment>
  );
};

export default AllReels;
