import React, { useState } from "react";
import "./App.css";
import data from "./components/data/videos.json";
import { Route, Switch } from "react-router-dom";
import AllReels from "./pages/AllReels";
import AllVideos from "./pages/AllVideos";
import NewReel from "./pages/NewReel";
import Navigation from "./components/layout/Navigation";

//Todo: get items for reels from server/storage
let reels = [];

function App() {
  const [reelItems, setReelItems] = useState(reels);
  const addNewReelHandler = (newReel) => {
    const reelData = {
      ...newReel,
      //Todo: Consider to use other way to generate id
      id: Math.random().toString(),
      videoIds: [],
    };
    // Change items of reels
    console.log("setReeItems in add");
    setReelItems((previousReels) => {
      return [reelData, ...previousReels];
    });
  };

  return (
    <div className="App">
      <Navigation />
      <Switch>
        <Route path="/" exact>
          <AllReels reels={reelItems} videos={data} />
        </Route>
        <Route path="/all-videos">
          <AllVideos items={data} />
        </Route>
        <Route path="/new-reel">
          <NewReel onAddNewReel={addNewReelHandler} />
        </Route>
      </Switch>
    </div>
  );
}

export default App;
