import { Fragment, useState } from "react";
import React from "react";
import Reels from "../components/Reels/Reels";
import ReelVideos from "../components/Reels/ReelVideos";
import NewReel from "./NewReel";

//Todo: get items for reels from App (server/storage)
let reels = [
  {
    id: 1,
    name: "Test Reel 001",
    standard: "PAL",
    definition: "SD",
    totalDuration: "00:01:20:30",
    videoIds: [1],
  },
];

const getReelVideos = (allVideos, reel) => {
  let reelVideos = [];
  allVideos.forEach((video) => {
    if (reel.videoIds.includes(video.id)) {
      reelVideos.push(video);
    }
  });
  return reelVideos;
};
// Todo: Apply styles
const AllReels = (props) => {
  let intialSelecteedReel = null;
  let initialFilteredVideo = [];

  if (reels.length > 0) {
    intialSelecteedReel = reels[0];
    initialFilteredVideo = getReelVideos(props.videos, intialSelecteedReel);
  }

  const [selectedReelItem, setSelectedReelItem] = useState(intialSelecteedReel);
  const [filteredVideos, setFilteredVideos] = useState(initialFilteredVideo);
  const [reelItems, setReelItems] = useState(reels);

  const addNewReelHandler = (newReel) => {
    const reelData = {
      ...newReel,
      //Todo: Consider to use other way to generate id
      id: Math.random().toString(),
      videoIds: [],
    };
    // Change items of reels
    setReelItems((previousReels) => {
      return [reelData, ...previousReels];
    });
    setSelectedReelItem();
  };
  const displayReelVideosHandler = (selectedReel) => {
    const reelVideos = getReelVideos(props.videos, selectedReel);
    setFilteredVideos(reelVideos);
    setSelectedReelItem(selectedReel);
  };

  const removeVideoHandler = (video, reel) => {
    let newVideoIds = [...reel.videoIds];
    const videoIdIndex = newVideoIds.indexOf(video.id);
    console.log(newVideoIds);
    console.log("inde");
    console.log(videoIdIndex);
    if (videoIdIndex !== -1) {
      newVideoIds.splice(videoIdIndex, 1);
    }
    console.log(newVideoIds);
    console.log(reelItems);
    const reels = [...reelItems];
    const resetReels = reels.map((reelItem) =>
      reelItem.id === reel.id
        ? { ...reelItem, videoIds: newVideoIds }
        : reelItem
    );
    const reelVideos = getReelVideos(props.videos, reel);
    setFilteredVideos(reelVideos);
    console.log(reelItems);
    setSelectedReelItem(reel);
    setReelItems(resetReels);
  };

  return (
    <Fragment>
      <NewReel onAddNewReel={addNewReelHandler} />
      <h1>All Reels</h1>
      {reelItems.length > 0 && (
        <div>
          <Reels
            items={reelItems}
            onSelectReel={displayReelVideosHandler}
            videos={props.videos}
            filteredVideos={filteredVideos}
            onChangeReelVideos={displayReelVideosHandler}
          />
        </div>
      )}
      <div>
        <ReelVideos
          reel={selectedReelItem}
          items={filteredVideos}
          onRemoveVideo={removeVideoHandler}
        />
      </div>
    </Fragment>
  );
};

export default AllReels;
